import { Component } from '@angular/core';

@Component({
  selector: 'app-filtre',
  templateUrl: './filtre.component.html',
  styleUrls: ['./filtre.component.css']
})
export class FiltreComponent {
  namefilter !: boolean;
  moleculefilter !: boolean;
  furnisherfilter !: boolean;
  filterByName(event : any){
    this.namefilter = event;
  }
  filterByMolecule(event : any){
    this.moleculefilter = event;
  }
  filterByFurnisher(event : any){
    this.furnisherfilter = event;
  }
}
<div clas="page">
    <form>
        <mat-checkbox (change)="filterByName($event)">Nom de médicament</mat-checkbox>
        <mat-checkbox (change)="filterByMolecule($event)">Nom de médicament</mat-checkbox>
        <mat-checkbox (change)="filterByFurnisher($event)">Nom de médicament</mat-checkbox>
        <button mat-button>Rechercher</button>
    </form>
</div>
